    private void generarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generarActionPerformed
           int numero;
    Random aleatorio = new Random();
    numero = Integer.parseInt(Numero1.getText());
    int matriz[][] = new int [5][5];

    // Generar la matriz con n√∫meros aleatorios
    for(int a = 0; a < matriz.length; a++){
        for(int b = 0; b < matriz[a].length; b++){
            matriz[a][b] = aleatorio.nextInt(numero);
        }
    }

    // Imprimir la matriz en el JTextArea
    StringBuilder stringBuilder = new StringBuilder();
    for(int a = 0; a < matriz.length; a++){
        for(int b = 0; b < matriz[a].length; b++){
            stringBuilder.append("[\t"+matriz[a][b]).append("\t]");
        }
        stringBuilder.append("\n");
    }
    Numeros.setText(stringBuilder.toString());

    }//GEN-LAST:event_generarActionPerformed

